{-# LANGUAGE GADTs, ExistentialQuantification #-}
module Main (main) where

import Control.Arrow
import Day1 (solve1_1, solve1_2)

data Showable where
    MkShowable :: Show a => a -> Showable

instance Show Showable where
  showsPrec p (MkShowable a) = showsPrec p a

main :: IO ()
main = mapM_ run input

run :: (Show s) => (String, String -> Either String s, String) -> IO ()
run (name, solve, x) = do
    putStrLn $ "Solution to puzzle " ++ name
    case solve x of
        Left msg -> putStr "ERROR: " >> putStrLn msg
        Right r -> print r

input :: [(String, String -> Either String Showable, String)]
input = [ ("1-1", packSolve solve1_1, input1)
        , ("1-2", packSolve solve1_2, input1)
        ]

